mapper.select:
SELECT * FROM project_node_result
WHERE projectID = #{id} AND nodeIndex = #{index} AND userID in

    #{item}

mapper.update:
UPDATE project_node_result
SET state = CASE

    WHEN projectID = #{item.projectID} AND nodeIndex =#{item.nodeIndex} AND userID = #{item.userID} THEN #{state}
,
SET  resultKey =CASE

    WHEN projectID = #{item.projectID} AND nodeIndex =#{item.nodeIndex} AND userID = #{item.userID} THEN #{resultKey}
,
SET  message =CASE

    WHEN projectID = #{item.projectID} AND nodeIndex =#{item.nodeIndex} AND userID = #{item.userID} THEN #{message}
,
SET  resultID =CASE

    WHEN projectID = #{item.projectID} AND nodeIndex =#{item.nodeIndex} AND userID = #{item.userID} THEN #{resultID}
,
SET  resultName =CASE

    WHEN projectID = #{item.projectID} AND nodeIndex =#{item.nodeIndex} AND userID = #{item.userID} THEN #{resultName}
,
